settings.custom_liquid:
{% if blog.articles.size > 0 %}
  <div class="blog-tags-filter">
    <h3>Filter by Types</h3>
    {% assign current_tag = current_tags.first %}
    <select id="blog-tag-filter" onchange="location = this.value;">
      <option value="{{ blog.url }}" {% if current_tag == blank %}selected{% endif %}>All </option>
      {% assign tags = "" %}
      {% for article in blog.articles %}
        {% for tag in article.tags %}
          {% unless tags contains tag %}
            {% assign tags = tags | append: ',' | append: tag %}
          {% endunless %}
        {% endfor %}
      {% endfor %}
      {% assign tag_list = tags | split: ',' | uniq %}

      {% for tag in tag_list %}
        {% if tag != "" %}
          <option value="{{ blog.url }}/tagged/{{ tag | handle }}" {% if current_tag == tag %}selected{% endif %}>{{ tag }}</option>
        {% endif %}
      {% endfor %}
    </select>
  </div>
{% endif %}
